package java_programs;

public class Concat_Three_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       String s = "ABC";
       String s1 = "XYZ";
       String s2 = "POR";
       
       String v = s.concat(s1).concat(s2);
       System.out.println(v);
	}

}
