package java_programs;

public class Swapping_without_Thirdvariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a =10;
     int b = 20;
     
     
     a=a+b;//30
     b=a-b;//10
     a=a-b;//20
     System.out.println("a is ="+ a);
     System.out.println("b is ="+ b);

	}

}
